<?php/* file created by Retailon 42*/class Retailon_Storeconf_Block_Adminhtml_Storeconf_Renderer_Storestatus extends Mage_Adminhtml_Block_Widget_Grid_Column_Renderer_Abstract{    public function render(Varien_Object $row)    {        $storeid = $row['store_name'];        $read = Mage::getSingleton('core/resource')->getConnection('core_read');        $write = Mage::getSingleton('core/resource')->getConnection('core_write');        $result = $read->fetchAll("select is_active from core_store where Store_id = ".$storeid);        if($result[0]['is_active'] > 0)        {            return "Enabled";        }        else        {            return "Disabled";        }    }}